mapper.select:
		select
		
		from wy_cons
		
		1=1
			
				and groupcode=#{groupCode,jdbcType=VARCHAR}
			
            
                and pid=#{pId,jdbcType=VARCHAR}
            
            
                and name=#{name,jdbcType=VARCHAR}
            
            
                and pid is null
            
			order by orderby

		select
		
		from wy_cons
		
		1=1
			
				and groupcode=#{groupCode,jdbcType=VARCHAR}
			
			
				and code=#{code,jdbcType=DECIMAL}			
			
            
                and name=#{name,jdbcType=VARCHAR}
