mapper.select:
select * from (select m.*,ROWNUM RN from
(select
,a.name cityname,b.name countryname,c.name c_name,
(SELECT COUNT(1) FROM wy_bbu WHERE relate_wybts=u.int_id) sharebbucount
from WY_BBU u
left join wy_city a on a.id=u.CITY_ID
left join wy_city b on b.id=u.COUNTY_ID
LEFT JOIN (SELECT CODE,NAME FROM wy_cons WHERE groupcode='ABANDONEDTYPE')c
ON c.code=u.DELETE_RESONCODE
)m

    1=1
    
        and int_id in(${ids})
    
    
        and COUNTY_ID in(${countryIds})
    
    
        and int_id = #{intId,jdbcType=DECIMAL}
    
    
        and NAME like #{name}
    
    
        and BSC_NAME like #{bscName}
    
    
        and BTS_ID = #{btsId,jdbcType=DECIMAL}
    
    
        and IS_SHARE=#{isShare,jdbcType=DECIMAL}
    
    
        and bbu_type in (${bbuType})
    
    
        and delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    
    
        and MANUAL_FLAG=#{manualFlag,jdbcType=DECIMAL}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd')>=#{startTime}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd') < #{endTime}
    
    
        and DELETE_RESONCODE=0
    
    
        and DELETE_RESONCODE!=0
    
    
        and RowNum < (#{start} + #{pagesize})
    

)

    
        RN >= #{start}

select count(*) from WY_BBU

    1=1
    
        and COUNTY_ID in(${countryIds})
    
    
        and NAME like #{name}
    
    
        and BSC_NAME like #{bscName}
    
    
        and BTS_ID = #{btsId,jdbcType=DECIMAL}
    
    
        and IS_SHARE=#{isShare,jdbcType=DECIMAL}
    
    
        and bbu_type in (${bbuType})
    
    
        and delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    
    
        and MANUAL_FLAG=#{manualFlag,jdbcType=DECIMAL}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd')>=#{startTime}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd') < #{endTime}

select,a.msc_name msc_name,a.usestatus usestatus,
a.DO_bts do_bts from WY_BBU u
left join c_bts a on a.int_id=u.related_bts

    1=1
    
        and u.relate_wybts=#{relateWyBts,jdbcType=DECIMAL}
    
    
        and u.COUNTY_ID in(${countryIds})
    
    
        and IS_SHARE=#{isShare,jdbcType=DECIMAL}
    
    
        and u.BBU_TYPE in (${bbuType})
    
    
        and u.delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    
    
        and MANUAL_FLAG=#{manualFlag,jdbcType=DECIMAL}
    

order by u.BBU_NO

select
from WY_BBU u

    1=1
    
        and u.COUNTY_ID=#{countryId}
    
    
        and u.IS_SHARE=#{isShare,jdbcType=DECIMAL}
    
    
        and u.BBU_TYPE in (${bbuType})
    
    
        and u.delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    
    
        and u.MANUAL_FLAG=#{manualFlag,jdbcType=DECIMAL}
    
    
        and u.NAME=#{name}
    

order by u.BSC_NAME,u.BTS_ID

SELECT a.int_id,a.name,e.name cityname,f.name countryname,b.msc_name,a.bsc_name,a.bts_id,b.usestatus,a.longitude,a.latitude,b.do_bts,a.bts_name, a.circuitroom_ownership,a.trans_ownership,c.install_pos_code,c.shar_flag,c.shar_name,c.address,c.open_time, a.vendor_btstype,c.mr_strut,q.name mr_strut_name,c.mr_length,c.mr_width,c.mr_high,c.mr_owner, c.wd_type,c.wd_model,c.wd_fac,c.wd_gds,c.tran_model,c.tran_fac,c.tran_upsitename,c.tran_downsitename,c.tran_netprotect, C.Tran_Isnode,c.tran_sitenum,c.sour_model,c.sour_fac,c.sour_module_model,c.sour_module_num,c.sour_capa, c.box_model,c.box_fac,c.cell_model,c.cell_fac,c.cell_capa,c.cell_num,c.cell_power,c.cell_duar,c.cell_ustime, c.ac_model,c.ac_fac,c.ac_num,c.dh_model,c.dh_fac,c.xf_model,c.xf_fac FROM wy_bbu a LEFT JOIN c_bts b ON b.int_id=a.related_bts LEFT JOIN wy_bbu_manual c ON c.int_id=a.int_id LEFT JOIN wy_city e ON a.city_id=e.id LEFT JOIN wy_city f ON a.county_id=f.id LEFT JOIN (SELECT CODE,NAME FROM wy_cons WHERE groupcode='MRSTRUT')q ON q.code=c.mr_strut
1=1 and a.delete_flag=0 and a.is_share=0 and a.bbu_type in(1,2)
and a.COUNTY_ID in(${countryIds})
and a.name like #{name}
and a.BSC_NAME like #{bscName}
and a.BTS_ID = #{btsId,jdbcType=DECIMAL}
and a.int_id = #{intId,jdbcType=DECIMAL}
ORDER BY a.city_id,a.county_id ASC

select * from (select m.*,ROWNUM RN from ( SELECT
${sqlsb} ,count(1) num FROM (SELECT a.int_id,a.name,e.name cityname,f.name countryname,a.city_id,b.msc_name,a.bsc_name,a.bts_id,b.usestatus,a.longitude,a.latitude,b.do_bts,a.bts_name, a.circuitroom_ownership,a.trans_ownership,a.Is_Share,a.Bbu_Type,c.install_pos_code,c.shar_flag,c.shar_name,c.address,c.open_time, a.vendor_btstype,c.mr_strut,q.name mr_strut_name,c.mr_length,c.mr_width,c.mr_high,c.mr_owner, c.wd_type,c.wd_model,c.wd_fac,c.wd_gds,c.tran_model,c.tran_fac,c.tran_upsitename,c.tran_downsitename,c.tran_netprotect, C.Tran_Isnode,c.tran_sitenum,c.sour_model,c.sour_fac,c.sour_module_model,c.sour_module_num,c.sour_capa, c.box_model,c.box_fac,c.cell_model,c.cell_fac,c.cell_capa,c.cell_num,c.cell_power,c.cell_duar,c.cell_ustime, c.ac_model,c.ac_fac,c.ac_num,c.dh_model,c.dh_fac,c.xf_model,c.xf_fac FROM wy_bbu a LEFT JOIN c_bts b ON b.int_id=a.related_bts LEFT JOIN wy_bbu_manual c ON c.int_id=a.int_id LEFT JOIN wy_city e ON a.city_id=e.id LEFT JOIN wy_city f ON a.county_id=f.id LEFT JOIN (SELECT CODE,NAME FROM wy_cons WHERE groupcode='MRSTRUT')q ON q.code=c.mr_strut WHERE a.delete_flag=0 )
1=1
and city_id in(${cityIds})
and is_share=#{isShare,jdbcType=DECIMAL}
and Bbu_Type in(${bbuType})
GROUP BY
${sqlsb}
order by ${orderCondition}
order by NUM DESC
)m
and RowNum < (#{start} + #{pagesize})
)
RN >= #{start}

