mapper.select:
select * from (select m.*,ROWNUM RN from
(select
,a.name cityname,b.name countryname,c.name c_name
from WY_CELL u
left join wy_city a on a.id=u.CITY_ID
left join wy_city b on b.id=u.COUNTRY_ID
left join wy_cons c on c.groupcode='ABANDONEDTYPE' and c.code=u.DELETE_RESONCODE
)m

    1=1
    
       and int_id in(${ids})
    
    
        and CITY_ID in(${cityId})
    
    
        and COUNTRY_ID in(${countryIds})
    
    
        and WY_BTS_INT_ID=#{btsIntId,jdbcType=DECIMAL}
    
    
        and int_id = #{intId,jdbcType=DECIMAL}
    
    
        and NAME like #{name,jdbcType=VARCHAR}
    
    
        and BSC_NAME like #{bscName,jdbcType=VARCHAR}
    
    
        and btsId = #{btsId,jdbcType=DECIMAL}
    
    
        and CI = #{ci,jdbcType=DECIMAL}
    
    
        and PN = #{pn,jdbcType=DECIMAL}
    
    
        and delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    
     
        and MANUAL_FLAG = #{manualFlag,jdbcType=DECIMAL}
    
    
        and DELETE_RESONCODE=0
    
    
        and DELETE_RESONCODE!=0
    
    
        and to_char(DELETETIME,'yyyy-MM-dd')>=#{startTime}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd') < #{endTime}
    
    
        and RowNum < (#{start} + #{pagesize})
    

)

    
        RN >= #{start}

select count(1) from c_carrier

    1=1
    
        and RELATED_CELL=#{cellId,jdbcType=DECIMAL}
    
    
        and CAR_TYPE=#{carType,jdbcType=VARCHAR}
    
    
        and CITY_ID in(${cityId})
    
    
        and delete_flag = #{deleteFlag,jdbcType=DECIMAL}

select count(1) from WY_CELL

    1=1
    
        and int_id = #{intId,jdbcType=DECIMAL}
    
    
        and COUNTRY_ID in(${countryIds})
    
    
        and WY_BTS_INT_ID=#{btsIntId,jdbcType=DECIMAL}
    
    
        and WY_BTS_INT_ID=#{btsIntId,jdbcType=DECIMAL}
    
    
        and CITY_ID in(${cityId})
    
    
        and NAME like #{name,jdbcType=VARCHAR}
    
    
        and BSC_NAME like #{bscName,jdbcType=VARCHAR}
    
    
        and btsId = #{btsId,jdbcType=DECIMAL}
    
    
        and CI = #{ci,jdbcType=DECIMAL}
    
    
        and PN = #{pn,jdbcType=DECIMAL}
    
    
        and delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    
    
        and MANUAL_FLAG = #{manualFlag,jdbcType=DECIMAL}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd')>=#{startTime}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd') < #{endTime}

SELECT a.int_id,a.name,e.name cityname,f.name countryname,a.CELLID,b.msc_name,a.bsc_name, a.btsid,a.ci,a.pn,(case(a.do_cell) when 0 then '1X' when 1 then 'DO' when 2 then '1X+DO' end) do_cell,a.is_indoor,a.is_rru,a.is_gf,d.name WY_BTS_NAME,b.bts_name,d.longitude,d.latitude,c.celltype,b.capacity,b.pn_inc,b.numfa,b.numfa_do, d.vendor_btstype,g.tower_type,g.tower_high,c.feederlength FROM wy_cell a LEFT JOIN c_cell b ON a.int_id=b.int_id LEFT JOIN wy_cell_manual c ON a.int_id=c.int_id LEFT JOIN wy_bts d ON a.WY_BTS_INT_ID=d.int_id LEFT JOIN wy_bts_manual g ON a.wy_bts_int_id=g.int_id LEFT JOIN wy_city e ON a.city_id=e.id LEFT JOIN wy_city f ON a.COUNTRY_ID=f.id
1=1 AND a.delete_flag=0
and a.COUNTRY_ID in(${countryIds})
and a.NAME like #{name,jdbcType=VARCHAR}
and a.BSC_NAME like #{bscName,jdbcType=VARCHAR}
and a.btsId = #{btsId,jdbcType=DECIMAL}
and a.CI = #{ci,jdbcType=DECIMAL}
and a.PN = #{pn,jdbcType=DECIMAL}
and a.int_id = #{intId,jdbcType=DECIMAL}
ORDER BY a.city_id,a.COUNTRY_ID ASC

select * from (select m.*,ROWNUM RN from ( SELECT
${sqlsb} ,count(1) num FROM (SELECT a.name, A.City_Id, A.Country_Id,e.name cityname,f.name countryname,a.CELLID,b.msc_name,a.bsc_name, a.btsid,a.ci,a.pn,(case(a.do_cell) when 0 then '1X' when 1 then 'DO' when 2 then '1X+DO' end) do_cell,a.is_indoor,a.is_rru,a.is_gf,d.name WY_BTS_NAME,b.bts_name,d.longitude,d.latitude,c.celltype,b.capacity,b.pn_inc,b.numfa,b.numfa_do, d.vendor_btstype,g.tower_type,g.tower_high,c.feederlength FROM wy_cell a LEFT JOIN c_cell b ON a.int_id=b.int_id LEFT JOIN wy_cell_manual c ON a.int_id=c.int_id LEFT JOIN wy_bts d ON a.WY_BTS_INT_ID=d.int_id LEFT JOIN wy_bts_manual g ON a.wy_bts_int_id=g.int_id LEFT JOIN wy_city e ON a.city_id=e.id LEFT JOIN wy_city f ON a.COUNTRY_ID=f.id where 1=1 AND a.delete_flag=0 )
1=1
and city_id in(${cityIds})
GROUP BY
${sqlsb}
order by ${orderCondition}
order by NUM DESC
)m
and RowNum < (#{start} + #{pagesize})
)
RN >= #{start}

