        and name like #{name}
    
    
        and BSC_NAME like #{bscName}
    
    
        and BTS_NAME like #{btsName}
    
    
        and BTS_ID =  #{btsId,jdbcType=DECIMAL}
    
    
        and delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    
    
        and MANUAL_FLAG=#{manualFlag,jdbcType=DECIMAL}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd')>=#{startTime}
    
    
        and to_char(DELETETIME,'yyyy-MM-dd') < #{endTime}

select

from WY_TUNEL u

    1=1
    
        and u.COUNTY_ID=#{countryId}
    
    
        and u.name=#{namejdbcType=VARCHAR}
    
    
        and u.delete_flag = #{deleteFlag,jdbcType=DECIMAL}
    

order by u.BSC_NAME, u.BTS_ID

select 

from WY_TUNEL u
where u.INT_ID = #{intId,jdbcType=DECIMAL}

SELECT ,e.name cityname,f.name countryname from wy_tunel u
LEFT JOIN wy_city e ON
u.city_id=e.id
LEFT JOIN wy_city f ON
u.COUNTY_ID=f.id

    1=1 and u.delete_flag=0
    
        and u.COUNTY_ID in(${countryIds})
    
    
        and u.name like #{name}
    
    
        and u.BSC_NAME like #{bscName}
    
    
        and u.BTS_ID = #{btsId,jdbcType=DECIMAL}
    
    
        and u.int_id = #{intId,jdbcType=DECIMAL}
    

ORDER BY u.city_id,u.county_id ASC

select * from (select m.*,ROWNUM RN from
(
SELECT

    ${sqlsb}
,count(1) num
FROM
(SELECT , e.name cityname,f.name countryname FROM wy_tunel u
LEFT JOIN wy_city e ON
u.city_id=e.id
LEFT JOIN wy_city f ON
u.county_id=f.id
WHERE u.delete_flag=0
)

    1=1
    
        and city_id in(${cityIds})
    

GROUP BY

    ${sqlsb}


    
        order by ${orderCondition}
    
    
        order by NUM DESC
    

)m

    
        and RowNum < (#{start} + #{pagesize})
    

)

    
        RN >= #{start}

select * from (select m.*,ROWNUM RN from ( SELECT
${sqlsb} ,count(1) num FROM (SELECT u.INT_ID,u.CITY_ID,u.NAME,u.BSC_NAME,u.BTSID,u.PN,u.CI,u.IS_RRU,u.IS_GF, , e.name cityname,f.name countryname,o.NAME PROP_NAME,p.NAME SHAREFLAG_NAME,q.NAME ROAD_ID_NAME FROM wy_cell u LEFT JOIN wy_tunel_manual tm ON tm.int_id=u.int_id LEFT JOIN wy_city e ON u.city_id=e.id LEFT JOIN wy_city f ON u.country_id=f.id LEFT JOIN (SELECT CODE,NAME FROM wy_cons WHERE groupcode='TUNELPROP')o ON o.code=tm.PROP LEFT JOIN (SELECT CODE,NAME FROM wy_cons WHERE groupcode='TUNEL_SHARE')p ON p.code=tm.SHAREFLAG LEFT JOIN(select ID,NAME from wy_lib_road where delete_flag=0)q ON q.ID=tm.ROAD_ID WHERE u.delete_flag=0 and u.is_indoor='隧' )
1=1
and city_id in(${cityIds})
GROUP BY
${sqlsb}
order by ${orderCondition}
order by NUM DESC
)m
and RowNum < (#{start} + #{pagesize})
)
RN >= #{start}
