select u.* from(select INT_ID,NAME, city_id, county_id,BSC_NAME,BTS_ID,(case is_indoor when '否' then 1 when '是' then 3 END) BTS_TYPE from wy_bts where delete_flag=0 UNION select INT_ID,NAME, city_id, county_id,BSC_NAME,BTS_ID,2 BTS_TYPE from wy_bbu where is_share=0 and bbu_no in(1,2) and delete_flag=0 UNION select INT_ID,NAME, city_id, county_id,BSC_NAME,BTS_ID,6 BTS_TYPE from wy_tunel where delete_flag=0)u
1=1
and u.name=#{name}
and u.BTS_TYPE=#{btsType}
and u.INT_ID=#{intId}

select * from (select m.*,ROWNUM RN from
(
select ,c.name cityName,d.name countryName from wy_bts_charge_list u
left join wy_city c on c.id=u.city_id
left join wy_city d on d.id=u.COUNTRY_ID

    
        and u.COUNTRY_ID in(${countryIds})
    
    
        and u.COST_TYPE=#{costType}
    
    
        and u.name like '%${btsName}%'
    
    
        and u.bsc_name=#{bscName}
    
    
        and u.bts_id=#{btsId}
    
    
        and u.BTS_TYPE=#{btsType}
    

)m

    
        and RowNum < (#{start} + #{pagesize})
    

)

    
        RN >= #{start}

