select count(1) from( select a.pay_cycle,a.pay_day,a.remind_user,a.remind_tel, b.int_id,b.name btsName,b.bsc_name bscName,b.bts_id btsId, c.name cityName,d.name countryName from wy_bts b left join (select u.* from WY_BTS_CHARGE u where u.BTS_TYPE = ${btsType} and u.cost_type = ${costType}) a on b.INT_ID = a.INT_ID left join wy_city c on c.id = b.city_id left join wy_city d on d.id = b.county_id
b.delete_flag=0
and b.COUNTY_ID in(${countryIds})
and b.name like '%${btsName}%'
and b.bsc_name=#{bscName}
and b.bts_id=#{btsId}
and b.is_indoor = #{isInDoor}
)

